mapper.select:
SELECT p.product_seq, p.product_subject, p.product_img1, to_char(p.product_price, 'FM99,999,999') ||'원' as product_price FROM product p WHERE 1=1
#{item}
CASE WHEN p.product_seq = to_number(#{item}) THEN #{index} ELSE -1 END

SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT p.product_seq, p.mem_id, p.cate_code, p.product_subject, p.product_content, p.product_img1, p.product_img2, p.product_img3, p.product_img4, p.product_img5, to_char(p.product_price, 'FM99,999,999') ||'원' as product_price , p.product_location, p.product_condition , p.product_delivery_fee , p.product_hashtag1, p.product_hashtag2, p.product_hashtag3, p.product_hashtag4, p.product_hashtag5, p.product_view, p.product_manage , ROUND((sysdate - p.product_logtime)*24*60) as product_logtime, s.store_nickname FROM product p, store s WHERE p.mem_id = s.mem_id
AND p.cate_code IN(select cate_code from category where cate_parent=#{cate_code}) AND p.cate_code=#{cate_code}
ORDER BY
p.product_logtime desc p.product_view asc p.product_price asc p.product_price desc
p.product_logtime desc
) a ) WHERE rn BETWEEN nvl(#{start},1) AND nvl(#{end},20)
