// 내상점-남의상점 구분
var loginId = $('.loginId').val();
var paramId = $('.hiddenId').val();

var userId;
if(loginId == paramId){ // 내 상점
	userId = loginId; // 둘 중 암거나 ㄱㅊ
}else if(loginId != paramId){ // 남의 상점
	userId = paramId;
}

// 기본 - 상품 리스트 가져오기
var sortNum = $('.hiddenSortNum').val(); // 기본은 최신순 0 


$(document).ready(function(){
	$.ajax({
		type: 'post',
		url: '/market/store/storeProductList',
		data: {'mem_id' : userId, 
			   'sortNum' : sortNum}, //정렬번호
		dataType: 'json',
		success : function(data){
			
			$('.productOne').remove(); // 창 이동 시 계속 append 되는 거 방지
			
			//상품 총 개수
			$('.productNum').text(data.productTotalA);
			// 나중에 그룹정렬별로 총 개수 수정
			$('.mainGroupNum').text(data.productTotalA);
			
			$.each(data.productList, function(index, items){
				$("<div class='productOne'/>").append($('<a/>',{ 
					class: 'productOneInner'+index,
					href: '#'
				}).append($('<div/>',{
					class: 'productImg'
				}).append($('<img/>',{
					src: '/market/storage/'+items.product_img1, 
					width: '194',
					height: '194',
					alt: '상품 이미지'
				})).append($('<div/>',{
					// 배송비
					class: 'deliveryFee'+index // 해당 index에만 효과 적용하기 위해
				})).append($('<div/>',{
					//판매상태
					class: 'sellCheck'+index
				}).append($('<div/>',{
					//children().first()
				})).append($('<img/>',{
					width: '11',
					heigth: '18'
				})).append($('<div/>',{
					//children().last()
				})))).append($('<div/>',{
					class: 'productNameWrap'
				}).append($('<div/>', {
					class: 'productName',
					text: items.product_subject
				})).append($('<div/>', {
					class: 'productDetail'
				}).append($('<div/>', {
					class: 'productPrice',
					text: items.product_price.toLocaleString()
				})).append($('<div/>',{
					class: 'productUpdate'
				}).append($('<span/>',{
					text: items.product_logtime //'며칠전' 으로 바꾸기
				}))))).append($('<div/>',{
					class: 'location',
					//text: items.product_location
				}).append($('<img/>',{
					//번장 위치 아이콘
					src: 'data:image/png;base64,',
					width: '15',
		            height: '17',
		            alt: '위치 아이콘'
		        })).append($('<div/>', {
		        	text: items.product_location
		        }))
		        )).appendTo($('.listWrap'));
				
				// css
				$('.location > img').next().css('position', 'absolute');
				
				$('.productOneInner'+index).css({'border': '1px solid rgb(238, 238, 238)',
				     'background': 'rgb(255, 255, 255)',
				     'display': 'block'});	
				
				// 클릭 -> 상세페이지 이동
				$('.productOneInner'+index).click(function(){
					$('.productOneInner'+index).attr('href','/market/product/productDetail?seq=' + items.product_seq);
				});
				
				
				// 배송비 *배송비 별도는 표시 없음
				if(items.product_delivery_fee == 1){
					$('.deliveryFee'+index).text('배송비포함');
					$('.deliveryFee'+index).css({'background':'rgba(0, 0, 0, 0.4)', 
												'font-size':'13px',
												'padding':'2px 3px 3px',
												'position':'absolute',
												'left':'10px',
												'bottom':'10px',
												'color':'rgb(255, 255, 255)'});
				}
				
				// 판매 상태;상태(1:판매중 2:예약중-거래약속중 3:판매완료) *판매 중은 표시 없음
				if(items.product_manage==1){
					$('.sellCheck'+index).remove();
				}else if(items.product_manage==2 || items.product_manage==3){
					$('.sellCheck'+index).css({'position':'absolute',
											  'top':'0px',
											  'left':'0px',
											  'width':'100%',
											  'height':'100%',
											  'background':'rgba(0, 0, 0, 0.6)',
											  'display':'flex',
											  '-webkit-box-pack':'center',
											  'justify-content':'center',
											  '-webkit-box-align':'center',
											  'align-items':'center',
											  'color':'rgb(255, 255, 255)'});
					if(items.product_manage==2){
						$('.sellCheck'+index).children().first().text('예약');
						$('.sellCheck'+index).children().last().text('완료');
						$('.sellCheck'+index+' > img').attr('src','data:image/png;base64,');
					}else if(items.product_manage==3){
						$('.sellCheck'+index).children().first().text('판매');
						$('.sellCheck'+index).children().last().text('완료');
						$('.sellCheck'+index+' > img').attr('src','data:image/png;base64,');
					}
				}
			});//each
		},
		error: function(err){
			console.log(err);
		}
	}); // ajax
});

